mapper.select:

select 
		rno, bno, reply, memberId, replydate, updatedate
from
	(
		select 
				/*+INDEX_DESC(reply idx_reply2) */ rownum rn, 
				rno, bno, reply, memberId, replydate, updatedate
		from 
				reply
		where 
				bno = #{bno}
			and rno > 0
			and rownum <= #{cri.pageNum} * #{cri.amount}
	) 
where 
		rn > (#{cri.pageNum} -1) * #{cri.amount}
